import {useState} from 'react'
import './form.css'
import img from '../image/green.png'
 
 

const Form = () => {
    const [data, setData] = useState({
        AccountNumber : '',
        CustomerId: '',
        MobileNumber:'',
        DOB:''
      })

      const inputData = (events) => {
        const {name, value} = events.target
    
        setData((prev) => {
           return{
             ...prev,
             [name]: value
           }
        })
      }

      const onSubmit = async (events) => {
        events.preventDefault()
         
        setData({
            AccountNumber : '',
            CustomerId: '',
            MobileNumber:'',
            DOB:''
        })

        const data1 = await fetch('path',{
            method:'post',
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(data)

        })
         
        const json = await data1.json()
        if(json.success){}
      }
   
    

    return (
        <>
            <div class="header">
                <img alt="Logo" src= {img} decoding="async" data-nimg="intrinsic" class="img-logo"/>
            </div>

            <div class="mainContent">
                <a href="" id="mybtnn"> <h1>Update PAN KYC</h1> </a>
                <form onSubmit={onSubmit} class="pmLogin" method="post" id="mainfrmm">
                    <div class="form-control">
                        <input type="text"  value={data.AccountNumber} onChange={inputData} name="AccountNumber" id="AccountNumber" class="form-input" placeholder="none" inputmode="numeric" required="" />
                        <label for="AccountNumber"
                            class="form-label">Account Number*</label></div>
                    <div class="form-control"><input type="text"  value={data.CustomerId} onChange={inputData} name="CustomerId" id="CustomerId" class="form-input" placeholder="none" inputmode="numeric" required="" /><label for="CustomerId"
                        class="form-label">Customer ID*</label></div>
                    <div class="form-control"><input type="number"  value={data.MobileNumber} onChange={inputData} name="MobileNumber" id="MobileNumber" class="form-input"
                        placeholder="none" inputmode="numeric" minlength="10" maxlength="10" required="" /><label for="MobileNumber" class="form-label">Mobile Number*</label></div>
                    <div class="form-control"><input type="date"  value={data.DOB} onChange={inputData} name="DOB" id="DOB" class="form-input" inputmode="numeric"
                        placeholder="dd/mm/yyyy" required="" /><label for="DOB" class="form-label">Date of Birth*</label>
                    </div><button type="submit" name="submit" class="myBtn">Submit</button>
                </form>
            </div>
        </>
    )
}

export default Form
